import React, { Component } from 'react';
import { Label, FormText, Button, Container, Form, FormGroup, Input} from "reactstrap";


class Rank extends Component {
  constructor(props) {
    super(props);
    this.state = {
      folder_path: "",
      fetchingData: false,
      responseDataRecieved: false,
      responseData: {}
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.tryAgain = this.tryAgain.bind(this);
  }

	handleSubmit(event) {

	    event.preventDefault();

	    this.setState({
	      fetchingData: true,
	      loadingmsg: "Fetching Personality Insights"
	    });

	    const formData = new FormData(event.target);

	    fetch(process.env.REACT_APP_WATSON_API_ENDPOINT, {
	      method: "POST",
	      body: formData
	    })
	      .then(function(response) {
	        return response.json();
	      })
	      .then(
	        function(data) {
	          if (data.error) {
	            this.setState(
	              {
	                fetchingData: false,
	                responseDataRecieved: false
	              },
	              function() {
	                alert(data.error + ". Please try again.");
	              }
	            );
	          } else {
	            this.setState({
	              fetchingData: false,
	              responseData: data,
	              responseDataRecieved: true
	            });
	          }
	        }.bind(this)
	      );
	}

	handleChange(event) {
	    this.setState({
	      folder_path: event.target.value
	    });
	    console.log(this.state.folder_path)
	}

	focusInput(component) {}
	tryAgain() {}
    handleChangeFile = (e) => {
        const file = e.target.files[0];
        if (!file) {
            return;
        }

        let src,preview,type=file.type;

        if (/^image\/\S+$/.test(type)) {
            src = URL.createObjectURL(file)
            preview = <img src={src} alt='' />
        }
        // 匹配类型为video/开头的字符串
        else if (/^video\/\S+$/.test(type)) {
            src = URL.createObjectURL(file)
            preview = <video src={src} autoPlay loop controls />
        }
        // 匹配类型为text/开头的字符串
        else if (/^text\/\S+$/.test(type)) {
            const self = this;
            const reader = new FileReader();
            reader.readAsText(file);
            //注：onload是异步函数，此处需独立处理
            console.log(this.result)
            console.log(file)
            reader.onload = function (e) {
                preview = <textarea value={this.result} readOnly></textarea>
                self.setState({ path: file.name, data: file, preview: preview })
            }
            return;
        } 

        this.setState({ path: file.name, data: file, preview: preview })
    }

	render() {
		return (
		  <div>
			<Form onSubmit={this.handleSubmit}>
			    <FormGroup>
			      <Label for="folder-path">Please choose folder which contains all the Personal Statements</Label>
			      <Input type="file" accept=".txt" id="folder-path" onChange={this.handleChangeFile}> </Input>
			    </FormGroup>

			    <Button
			      type="submit"
			      size="lg">
			      Analyse
			    </Button>
			</Form>
		  </div>
		);
	}
}

export default Rank;
