import React, { Component } from 'react';
import { Label, FormText, Button, Container, Form, FormGroup, Input} from "reactstrap";


class Rank extends Component {
  constructor(props) {
    super(props);
    this.state = {
      folder_path: "",
      ps_content: "",
      fetchingData: false,
      responseDataRecieved: false,
      responseData: {}
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.tryAgain = this.tryAgain.bind(this);
  }

	handleSubmit(event) {

	    event.preventDefault();

	    this.setState({
	      fetchingData: true,
	      loadingmsg: "Fetching Personality Insights"
	    });

	    const formData = new FormData(event.target);

	    fetch(process.env.REACT_APP_WATSON_API_ENDPOINT, {
	      method: "POST",
	      body: formData
	    })
	      .then(function(response) {
	        return response.json();
	      })
	      .then(
	        function(data) {
	          if (data.error) {
	            this.setState(
	              {
	                fetchingData: false,
	                responseDataRecieved: false
	              },
	              function() {
	                alert(data.error + ". Please try again.");
	              }
	            );
	          } else {
	            this.setState({
	              fetchingData: false,
	              responseData: data,
	              responseDataRecieved: true
	            });
	          }
	        }.bind(this)
	      );
	}

	handleChange(event) {
        const file = event.target.files[0];
        if (!file) {
            return;
        }

        const reader = new FileReader();
        // Asynchronous function
        reader.onloadend = () => {
        	const content = reader.result;
        	// console.log(content)

			// Upload formdata to api
	        const url = 'http://personality-insights.shahabqamar.com/api/';
	        const form = new FormData();
	        form.append('raw-text-content', content);
	        // console.log(form)

	        fetch(url, {
	            method: 'POST',
	            body: form
	        })
	        .then(function(response) {
		        return response.json();
		        })
	        .then(
		        function(data) {
	        	  // add more content here
		          console.log(data)
		        }
      		);
        }
        reader.readAsText(file);
	}


	render() {
		return (
		  <div>
			<Form onSubmit={this.handleSubmit}>
			    <FormGroup>
			      <Label for="folder-path">Please choose folder which contains all the Personal Statements</Label>
			      <Input type="file" accept=".txt" id="folder-path" onChange={this.handleChange}/> 
			    </FormGroup>

			    <Button
			      type="submit"
			      size="lg">
			      Analyse
			    </Button>
			</Form>
		  </div>
		);
	}
}

export default Rank;
