import React, { Component } from 'react';
import { Label, FormText, Button, Container, Form, FormGroup, Input} from "reactstrap";
import ReactTable from 'react-table'
import "react-table/react-table.css"

class Rank extends Component {
  constructor(props) {
    super(props);
    this.state = {
      folder_path: "",
      ps_content: "",
      candidate_data: []
    };
    this.handleChange = this.handleChange.bind(this);
  }


	handleChange(event) {

		for (var i = 0; i < event.target.files.length; i++) {
			const file = event.target.files[i];

	        if (!file) {
	            return;
	        }

	        const reader = new FileReader();
	        // Asynchronous function
	        reader.onloadend = () => {
	        	const content = reader.result;
	        	// console.log(content)
	        	this.setState({ps_content: content});


				// Upload formdata to api
		        const url = 'http://personality-insights.shahabqamar.com/api/';
		        const form = new FormData();
		        form.append('raw-text-content', content);
		        // console.log(form)
		        const self = this;
		        fetch(url, {
		            method: 'POST',
		            body: form
		        })
		        .then(function(response) {
			        return response.json();
			        })
		        .then(
			        function(data) {
		        	  // add more content here
			          console.log(data);
			          let temp_data = {
			          		name: file.name,
						    openness: data["personality"][0]["percentile"],
						    conscientiousness: data["personality"][1]["percentile"],
						    extraversion: data["personality"][2]["percentile"],
						    agreeableness: data["personality"][3]["percentile"],
						    neuroticism: data["personality"][4]["percentile"]
						};

		          	  console.log(temp_data);

		          	  // append to candidate_data

	          	  		var newArr = self.state.candidate_data;
	          	  		newArr.push(temp_data)
		          	  	self.setState({candidate_data: newArr})
		          	  	console.log(self.state.candidate_data)

			        }
	      		)
	        }
	        reader.readAsText(file);
		}
	}

	render() {
		return (
		  <div>
			<Form onSubmit={this.handleSubmit}>
			    <FormGroup>
					<Label for="folder-path">Please choose folder which contains all the Personal Statements</Label>
					<Input type="file" accept=".txt" id="folder-path" onChange={this.handleChange} multiple/> 
			    </FormGroup>

			    <Button
			      type="submit"
			      size="lg">
			      Analyse
			    </Button>
			</Form>
			<Container className="rank-table">
		        <ReactTable
					columns={[
					{
					  Header: "Name",
					  accessor: "name"
					},
					{
					  Header: "Openness",
					  accessor: "openness",
					},
					{
					  Header: "Conscientiousness",
					  accessor: "conscientiousness",
					},
					{
					  Header: "Extraversion",
					  accessor: "extraversion"
					},
					{
					  Header: "Agreeableness",
					  accessor: "agreeableness"
					},
					{
					  Header: "Emotional Range",
					  accessor: "neuroticism"
					}
					]}
					data={this.state.candidate_data}
					defaultPageSize={10}
					className="-striped -highlight"
				/>
			</Container>

		  </div>
		);
	}
}

export default Rank;
