import React, { Component } from 'react';
import { Label, FormText, Button, Container, Form, FormGroup, Input} from "reactstrap";


class Rank extends Component {
  constructor(props) {
    super(props);
    this.state = {
      folder_path: "",
      fetchingData: false,
      responseDataRecieved: false,
      responseData: {}
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.tryAgain = this.tryAgain.bind(this);
  }

	handleSubmit(event) {

	    event.preventDefault();

	    this.setState({
	      fetchingData: true,
	      loadingmsg: "Fetching Personality Insights"
	    });

	    const formData = new FormData(event.target);

	    fetch(process.env.REACT_APP_WATSON_API_ENDPOINT, {
	      method: "POST",
	      body: formData
	    })
	      .then(function(response) {
	        return response.json();
	      })
	      .then(
	        function(data) {
	          if (data.error) {
	            this.setState(
	              {
	                fetchingData: false,
	                responseDataRecieved: false
	              },
	              function() {
	                alert(data.error + ". Please try again.");
	              }
	            );
	          } else {
	            this.setState({
	              fetchingData: false,
	              responseData: data,
	              responseDataRecieved: true
	            });
	          }
	        }.bind(this)
	      );
	}

	handleChange(event) {
	    this.setState({
	      folder_path: event.target.value
	    });
	}

	focusInput(component) {}
	tryAgain() {}
  	handleChangeFile(file) {
        const reader = new FileReader();
        reader.readAsText(file);
        //注：onload是异步函数，此处需独立处理
  	}

	render() {
		return (
		  <div>
			<Input type="file" accept=".txt"> </Input>

			<Form onSubmit={this.handleSubmit}>
			    <FormGroup>
			      <Label for="folder-path">Please choose folder which contains all the Personal Statements</Label>
			      <Input type="text" name="text" id="folder-path" onChange={this.handleChange}/>
			      <Input type="file" accept=".txt"> </Input>
			    </FormGroup>

			    <Button
			      type="submit"
			      size="lg">
			      Analyse
			    </Button>
			</Form>
		  </div>
		);
	}
}

export default Rank;
