import React, { Component } from 'react';
import { Label, FormText, Button, Container, Form, FormGroup, Input} from "reactstrap";
import TextFileReader from 'react-text-file-reader'


class Rank extends Component {
  constructor(props) {
    super(props);
    this.state = {
      folder_path: "",
      fetchingData: false,
      responseDataRecieved: false,
      responseData: {}
    };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.tryAgain = this.tryAgain.bind(this);
  }

	handleSubmit(event) {

	    event.preventDefault();

	    this.setState({
	      fetchingData: true,
	      loadingmsg: "Fetching Personality Insights"
	    });

	    const formData = new FormData(event.target);

	    fetch(process.env.REACT_APP_WATSON_API_ENDPOINT, {
	      method: "POST",
	      body: formData
	    })
	      .then(function(response) {
	        return response.json();
	      })
	      .then(
	        function(data) {
	          if (data.error) {
	            this.setState(
	              {
	                fetchingData: false,
	                responseDataRecieved: false
	              },
	              function() {
	                alert(data.error + ". Please try again.");
	              }
	            );
	          } else {
	            this.setState({
	              fetchingData: false,
	              responseData: data,
	              responseDataRecieved: true
	            });
	          }
	        }.bind(this)
	      );
	}

	handleChange(event) {
	    this.setState({
	      folder_path: event.target.value
	    });
	}

	focusInput(component) {}
	tryAgain() {}
  	handleClick() {
        const reader = new FileReader();
        reader.readAsText(file);
        //注：onload是异步函数，此处需独立处理
        reader.onload = function (e) {
            preview = <textarea value={this.result} readOnly></textarea>
            self.setState({ path: file.name, data: file, preview: preview })
        }
  	}

	handleChangeFile = (file) => {
		fileData = new FileReader();
		fileReader.readAsText(file);
	}


  render() {
    return (
      <div>
        <Input type="file" accept=".txt" onChange={e => handleChangeFile(e.target.files[0])}> </Input>

      <Form onSubmit={this.handleSubmit}>
        <FormGroup>
          <Label for="folder-path">Text</Label>
          <Input type="text" name="text" id="folder-path" onChange={this.handleChange}/>
        </FormGroup>

        <Button
          type="submit"
          size="lg">
          Analyse
        </Button>
      </Form>

      </div>
    );
  }
}

export default Rank;
